css:
.list-tree.has-collapsable-children .list-nested-item > .list-item.loading:before {
  content: "\f087";
}
/*
 * Styles below taken from Atom's [tree-view][0] to ensure nuclide-file-tree looks exactly the same.
 *
 * [0] https://github.com/atom/tree-view
 */
.tree-view {
  flex-grow: 1;
  flex-shrink: 0;
  min-width: -webkit-min-content;
  min-height: -webkit-min-content;
  padding-left: 5px;
  padding-right: 10px;
  position: relative;
  width: 100%;
}
.tree-view .header {
  position: relative;
}
.tree-view .list-tree {
  position: static;
}
.tree-view .entry:before {
  content: '';
  position: absolute;
}
.nuclide-file-tree-filter {
  font-weight: bold;
  position: absolute;
  margin-top: 10px;
  left: 10px;
  z-index: 1;
  background: #2e2e2e;
  padding: 5px 10px;
  border: 1px solid #1c1c1c;
  transition: opacity 0.5s;
  display: none;
}
.nuclide-file-tree-filter.not-found {
  color: #ff6347;
}
.nuclide-file-tree-entry-highlight {
  background: #383838;
  border-radius: 3px;
}
.nuclide-file-tree-toolbar {
  margin-top: 10px;
  position: absolute;
  right: 30px;
  z-index: 1;
}
.nuclide-file-tree-toolbar-container {
  display: flex;
  flex: 1;
  flex-direction: column;
  min-height: 0;
  width: 100%;
  background-color: #262626;
}
.nuclide-file-tree-toolbar-fader {
  opacity: 0;
  transition: opacity 0.35s ease-in-out;
}
.nuclide-file-tree-toolbar-container:hover .nuclide-file-tree-toolbar-fader {
  opacity: 1;
}
.nuclide-file-tree .list-item .nuclide-ui-checkbox-label {
  margin-right: 5px;
}
.nuclide-file-tree-softened {
  opacity: 0.4;
}
.nuclide-file-tree-toolbar-icon:before,
.nuclide-file-tree-toolbar-icon {
  margin-right: 0;
}
.nuclide-file-tree-working-set-name-missing {
  visibility: hidden;
}
.nuclide-file-tree-toolbar-icon:hover + .nuclide-file-tree-working-set-name-missing {
  visibility: visible;
}
.nuclide-file-tree-connection-title {
  margin-left: 5px;
}
.nuclide-file-tree-open-files {
  display: flex;
  max-height: 30vh;
}
.nuclide-file-tree-open-files-list {
  padding-left: 5px;
  padding-right: 10px;
  position: relative;
  width: 100%;
}
.nuclide-file-tree-open-files-list .list-item:hover {
  color: #dbdbdb;
}
.nuclide-file-tree-open-files-list .icon-primitive-dot:before,
.nuclide-file-tree-open-files-list .icon-x:before {
  font-size: 12px;
  height: 12px;
  width: 12px;
}
.nuclide-file-tree-open-files-row {
  display: flex;
  width: 100%;
}
.nuclide-file-tree-section-caption {
  padding-left: 5px;
  padding-right: 5px;
}
