css:
atom-workspace .horizontal .vertical {
  position: relative;
}
background-tips {
  display: block;
  pointer-events: none;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  overflow: hidden;
}
background-tips .background-message {
  -webkit-user-select: none;
  cursor: default;
}
background-tips .background-message .message {
  padding: 0 30px;
  opacity: 0;
  transition: opacity 0.3s ease-in-out;
}
background-tips .background-message .message.fade-in {
  opacity: 1;
}
background-tips .background-message .message .keystroke {
  border: 2px solid;
  padding: 0 5px;
  border-radius: 9px;
  font-family: "Helvetica Neue", Arial, sans-serif;
}
