css:
.nuclide-find-references {
  background-color: #2e2e2e;
  color: #a8a8a8;
  font-size: 12px;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: -webkit-flex;
  flex-direction: column;
}
.nuclide-find-references-loading {
  margin: auto;
}
.nuclide-find-references-count {
  -webkit-flex-shrink: 0;
}
.nuclide-find-references-files {
  padding: 0 12px;
  position: relative;
  overflow: auto;
  -webkit-flex: 1;
}
.nuclide-find-references-filename {
  font-size: 1.1em;
  word-break: break-all;
}
.nuclide-find-references-ref-count {
  margin-left: 6px;
  position: relative;
  top: -2px;
}
.nuclide-find-references-refs {
  margin: 4px 0 0 16px;
  padding: 0;
}
.nuclide-find-references-ref {
  margin-bottom: 12px;
  color: rgba(168, 168, 168, 0.6);
}
.nuclide-find-references-ref-name {
  cursor: pointer;
}
.nuclide-find-references-ref-name:hover {
  text-decoration: underline;
}
.nuclide-find-references-file-preview {
  display: flex;
  margin-top: 4px;
}
.nuclide-find-references-file-preview atom-text-editor[mini] {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  flex: 1;
  font-size: 12px;
  line-height: 25px;
  max-height: none;
}
.nuclide-find-references-file-preview atom-text-editor::shadow .highlight.reference .region {
  background-color: rgba(255, 255, 255, 0.2);
}
.nuclide-find-references-line-number-column {
  background: #1c1c1c;
  border-top-left-radius: 2px;
  border-bottom-left-radius: 2px;
  font-family: Menlo, Monaco, Consolas, monospace;
  padding: 0 0.5em;
}
.nuclide-find-references-line-number {
  color: #ffffff;
  line-height: 25px;
}
