css:
.nuclide-debugger-container {
  display: flex;
}
.nuclide-debugger-root {
  display: flex;
  flex-direction: column;
  flex: 1;
}
.nuclide-debugger-root select.form-control {
  color: #a8a8a8;
  background-color: #3d3d3d;
  border-color: #1c1c1c;
}
.nuclide-debugger-root .control-bar {
  display: flex;
  flex-direction: row-reverse;
}
.nuclide-debugger-root-close-button {
  background: transparent;
  border: none;
  padding: 0;
  position: absolute;
  top: 2.5em;
  right: 0.5em;
  z-index: 1;
}
.nuclide-debugger-root .inspector {
  display: flex;
  flex-direction: column;
  flex: 1;
}
.nuclide-debugger-container-old-enabled {
  display: flex;
  flex-grow: 1;
}
.nuclide-debugger-container-new {
  flex-grow: 1;
  flex-basis: 0;
  overflow: auto;
}
.nuclide-debugger-webview-hidden {
  height: 0;
  overflow: hidden;
}
/*
Hack for flexbox + vertical scaling with webview shadow dom.
Use flexbox cross-axis stretch to fill the vertical space.
*/
.nuclide-debugger-webview {
  display: flex;
  flex-direction: row;
  align-children: stretch;
  align-items: stretch;
  flex: 1 0 auto;
}
.nuclide-debugger-webview::shadow object {
  flex: 1 0 auto;
  height: auto !important;
}
/* Watch Expressions */
.nuclide-debugger-atom-expression-value-list {
  margin: 0 0.5em;
}
.nuclide-debugger-atom-expression-value-row {
  align-items: center;
  display: flex;
  flex-direction: row;
  font-family: Menlo, Monaco, Consolas, monospace;
  justify-content: space-between;
  min-height: 23px;
  overflow: auto;
}
.nuclide-debugger-atom-expression-value-content {
  display: flex;
  flex-direction: row;
  flex-grow: 1;
}
.nuclide-debugger-atom-watch-expression-xout {
  cursor: pointer;
  opacity: 0;
}
.nuclide-debugger-atom-watch-expression-row:hover {
  background-color: #383838;
}
.nuclide-debugger-atom-watch-expression-row:hover .nuclide-debugger-atom-watch-expression-xout {
  opacity: 0.5;
}
.nuclide-debugger-atom-watch-expression-xout.nuclide-debugger-atom-watch-expression-xout:hover {
  opacity: 1;
}
.nuclide-debugger-atom-watch-expression-input {
  margin-left: -0.5em;
}
.nuclide-debugger-atom-watch-expression-add-new-input {
  margin-top: 0.5em;
}
/* Call stack */
.nuclide-debugger-atom-callstack-item {
  display: flex;
  justify-content: space-between;
}
.nuclide-debugger-atom-callstack-name {
  font-family: Menlo, Monaco, Consolas, monospace;
}
