css:
.show-todo-preview {
  background-color: #2e2e2e;
  color: #a8a8a8;
  overflow: scroll;
}
.show-todo-preview .todo-font {
  font-size: 14px;
  font-weight: bold;
}
.show-todo-preview .todo-loading {
  padding: 10px;
}
.show-todo-preview .input-block {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
}
.show-todo-preview .input-block .input-block-item {
  display: flex;
  flex: 1 1 200px;
  padding: 5px 10px;
}
.show-todo-preview .input-block .input-block-item .btn-group {
  display: flex;
  flex: 1 1 220px;
}
.show-todo-preview .input-block .input-block-item .btn-group .btn {
  flex: 1 1 200px;
  padding: 0;
  text-align: center;
}
.show-todo-preview .input-block .input-block-item .btn-group .btn:first-child {
  flex: 1 1 500px;
}
.show-todo-preview .input-block .input-block-item--flex {
  flex: 100 1 200px;
  position: relative;
}
.show-todo-preview .input-block .input-block-item--flex atom-text-editor {
  width: 100%;
}
.show-todo-preview .input-block.todo-info-block {
  padding-left: 5px;
}
.show-todo-preview .todo-options {
  border: 2px solid #3b3b3b;
  margin: 5px 10px;
  padding: 0 10px;
}
.show-todo-preview .todo-options .option {
  display: flex;
  align-items: center;
}
.show-todo-preview .todo-options .option > h2 {
  font-size: 14px;
  font-weight: bold;
  flex: 1 1 120px;
}
.show-todo-preview .todo-options .option > div {
  word-wrap: break-word;
  flex: 100;
}
.show-todo-preview .todo-options .items-on-table .badge,
.show-todo-preview .todo-options .items-off-table .badge {
  font-size: 14px;
  font-weight: bold;
  width: 80px;
  margin: 5px 10px 5px 0;
  cursor: move;
}
.show-todo-preview .todo-options .items-on-table .ghost,
.show-todo-preview .todo-options .items-off-table .ghost {
  opacity: 0.3;
}
.show-todo-preview .todo-table {
  margin: 5px 10px;
}
.show-todo-preview .todo-table table {
  width: 100%;
}
.show-todo-preview .todo-table tr:nth-child(2n + 1) {
  background-color: #3b3b3b;
}
.show-todo-preview .todo-table tr:hover {
  background-color: #484848;
}
.show-todo-preview .todo-table td + td,
.show-todo-preview .todo-table th + th {
  border-left: 1px groove #a8a8a8;
}
.show-todo-preview .todo-table th,
.show-todo-preview .todo-table td {
  padding: 10px 6px;
  word-break: break-word;
  cursor: pointer;
}
.show-todo-preview .todo-table th {
  font-size: 14px;
  font-weight: bold;
  min-width: 75px;
}
.show-todo-preview .todo-table th .sort-asc,
.show-todo-preview .todo-table th .sort-desc {
  float: right;
  pointer-events: none;
}
.show-todo-preview .todo-table th .sort-asc:not(.active),
.show-todo-preview .todo-table th .sort-desc:not(.active) {
  opacity: 0.2;
}
.show-todo-preview .todo-table th .sort-desc {
  margin: -6px -16px 0 0;
}
.show-todo-preview code {
  margin-right: 2px;
  color: #a8a8a8;
}
.theme-atom-material-ui .todo-table tr:nth-child(2n + 1),
.theme-atom-material-ui .todo-table tr:hover {
  background-color: #1c1c1c;
}
