css:
.nuclide-build-toolbar {
  border-bottom: 1px solid #1c1c1c;
  position: relative;
  z-index: 3;
}
.nuclide-build-progress-bar {
  width: 100%;
  height: 2px;
  position: absolute;
  bottom: 0;
}
.nuclide-build-progress-bar[hidden] {
  display: none;
}
.nuclide-build-progress-bar.indeterminate {
  background-image: repeating-linear-gradient(90deg, #0088ff 0%, #0088ff 25%, transparent 25%, transparent 100%);
  background-size: 1000px 100%;
  animation: animate-indeterminate-bar 8s linear infinite;
}
.nuclide-build-progress-bar.indeterminate:before {
  content: ' ';
  position: absolute;
  width: 100%;
  height: 100%;
  background-image: repeating-linear-gradient(90deg, rgba(0, 136, 255, 0.3) 0%, rgba(0, 136, 255, 0.3) 25%, transparent 25%, transparent 100%);
  background-size: 1800px 100%;
  animation: animate-indeterminate-bar-2 8s linear infinite;
}
@keyframes animate-indeterminate-bar {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1000px 0;
  }
}
@keyframes animate-indeterminate-bar-2 {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 1800px 0;
  }
}
.nuclide-build-progress-bar-bar {
  position: absolute;
  height: 100%;
  left: 0;
  width: 0;
  transition: width 0.3s ease-out;
  background-color: #0088ff;
}
.nuclide-build-toolbar-contents {
  display: flex;
  align-items: center;
}
.btn.btn.nuclide-build-system-button {
  align-items: center;
  padding-right: 0;
}
.btn.btn.nuclide-build-system-button .icon-path-fill {
  fill: #a8a8a8;
}
.nuclide-build-system-button-icon-wrapper {
  display: inline-block;
  height: 16px;
  vertical-align: middle;
}
.nuclide-build-tool-bar-button[hidden] {
  display: none;
}
.nuclide-build-tool-bar-button[hidden] {
  display: none;
}
.nuclide-build-system-button-arrow {
  padding: 0 0.8em;
}
.nuclide-build-system-button-arrow:before {
  font-size: 11px;
  margin: 0;
  padding: 0;
  width: initial;
}
