css:
.nuclide-dropdown-container {
  display: inline-block;
  position: relative;
}
.nuclide-dropdown-container > .icon {
  pointer-events: none;
  position: absolute;
  right: 0;
  top: 50%;
  transform: translateY(-50%);
}
.nuclide-dropdown-container > .icon::before {
  font-size: 11px;
}
.nuclide-dropdown {
  display: flex;
}
/* Match specificity of `.btn.btn-*` to override padding set by that selector. */
.btn.btn.nuclide-dropdown {
  /* Provide space for the right .icon */
  padding-right: 2.4em;
  -webkit-appearance: none;
}
.nuclide-dropdown-flat {
  background: transparent;
  border: none;
  border-radius: 0;
  border-right: 1px solid #1c1c1c;
  /* Provide space for the right .icon */
  padding-right: 2.4em;
  -webkit-appearance: none;
}
