css:
.nuclide-ui-panel-component {
  cursor: default;
  -webkit-user-select: none;
  white-space: nowrap;
  display: flex;
  align-items: stretch;
}
.nuclide-ui-panel-component.left {
  flex-direction: row-reverse;
  height: inherit;
}
.nuclide-ui-panel-component.bottom {
  flex-direction: column;
  width: 100%;
}
.nuclide-ui-panel-component.right {
  flex-direction: row;
  height: inherit;
}
/*
 * The style must be added because the browser-provided style targets `[hidden]`, which is less
 * specific than `.nuclide-ui-panel-component`.
 */
.nuclide-ui-panel-component[hidden] {
  display: none;
}
.nuclide-ui-panel-component-resize-handle {
  width: auto;
  height: auto;
  flex: 0 0 auto;
}
.nuclide-ui-panel-component-resize-handle.left {
  width: 4px;
  cursor: col-resize;
}
.nuclide-ui-panel-component-resize-handle.bottom {
  height: 4px;
  cursor: row-resize;
}
.nuclide-ui-panel-component-resize-handle.right {
  width: 4px;
  cursor: col-resize;
}
.nuclide-ui-panel-component-cursor-overlay {
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 4;
}
.nuclide-ui-panel-component-cursor-overlay.left {
  cursor: col-resize;
}
.nuclide-ui-panel-component-cursor-overlay.bottom {
  cursor: row-resize;
}
.nuclide-ui-panel-component-cursor-overlay.right {
  cursor: col-resize;
}
